import { postFile, getFile } from '../helpers/fetch';

// const baseUrl = 'https://backend-post-application.herokuapp.com/api/users/';
const baseUrl = 'http://localhost:5000/api/uploads/';

// Post
const updateImageProfile = (fileToUpload: File, userId: string, fileName: string) => postFile(`${baseUrl}`, fileToUpload, userId, fileName);

// Get
const getById = (id: string) => postFile(`${baseUrl}`, fileToUpload, userId, fileName);

export { updateImageProfile };
