import React, {
  useState, useEffect, useRef, useContext,
} from 'react';
import { FiUser } from 'react-icons/fi';
import styles from './ConfigureProfile.module.scss';
import { UserContext } from '../../../containers/App';
import profileImage from '../../../assets/images/profileImage.png';
import TextInput from '../../TextInput';

const ConfigureProfile = () => {
  const [showConfiguration, setShowConfiguration] = useState(false);

  // Reads current connected user from Context
  const contextUser = useContext(UserContext);

  // Ref for the prfile configuration window
  const wrapperRef = useRef<HTMLDivElement>(null);

  // Closes the profile configuration window if the user clicks outside
  useEffect(() => {
    // Detects if clicked on outside of element
    const handleClickOutside = (event: MouseEvent) => {
      // As Node used since event.target cannot be inferred to be a Node. It could have other types
      if (wrapperRef.current && !wrapperRef.current.contains(event.target as Node)) {
        setShowConfiguration(false);
      }
    };
    // Bind the event listener
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      // Unbind the event listener on clean up
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [wrapperRef]);

  return (
    <div className={styles.configureProfileContainer}>
      <div>
        <button
          className={styles.iconButton}
          onClick={() => setShowConfiguration(true)}
        >
          <FiUser className={styles.profileIcon} />
        </button>
        <div
          ref={wrapperRef}
          className={showConfiguration ? styles.configurationTargetShow : styles.configurationTargetHide}
        >
          <div
            className={styles.imageWrapper}
            style={{
              backgroundImage: contextUser?.user?.profileImage?.url && contextUser?.user?.profileImage?.url !== ''
                ? `url(${contextUser?.user?.profileImage?.url})` : `url(${profileImage})`,
            }}
          />
          <input
            type="text"
            value={contextUser?.user?.description || 'What is going on?'}
          />
        </div>
      </div>
    </div>
  );
};

export default ConfigureProfile;
