import React, { useState, useEffect } from 'react';
import style from './ProfileHeader.module.scss';
import { User } from '../../../../interfaces/User';
import * as userEndpoints from '../../../../endpoints/user';

type ProfileHeaderProps = {
  userId: string,
}

const ProfileHeader = ({ userId }: ProfileHeaderProps) => {
  const [user, setUser] = useState<User>();

  // Reads user from database
  useEffect(() => {
    const getUser = async () => {
      const userFromDB = await userEndpoints.getSingle(userId);
      setUser(userFromDB);
    };
    getUser();
  }, [user]);

  return (
    <div className={style.container}>
      <p className={style.name}>{`${user?.name} ${user?.surname}`}</p>
      <p className={style.description}>{user?.description}</p>
      <ul className={style.infoWrapper}>
        <li> 22 posts</li>
        <li>{user?.followers?.length}  followers</li>
        <li>{user?.following?.length}   following</li>
      </ul>
    </div>
  );
};

export default ProfileHeader;
