import React, { useCallback, useContext } from 'react';
import styles from './ProfileCard.module.scss';
import Button from '../Button';
import profileImage from '../../assets/images/profileImage.png';
import { UserContext } from '../../containers/App';

type ProfileCardProps = {
  name: string,
  description?: string,
  pictureUrl?: string,
  surname: string,
  textButton?: string,
  colorButton?: 'blue' | 'red';
  onClick?: (event: React.MouseEvent<HTMLButtonElement>) => void,
  backgroundFull?: boolean,
  id: string,
}

const ProfileCard = ({
  name, surname, description = '', pictureUrl, onClick, textButton, colorButton, backgroundFull, id,
}: ProfileCardProps) => {
  // const getUsersPosts = (userId: string) => get(`${baseUrl}${userId}/posts`);
  // Reads current connected user from Context
  const contextUser = useContext(UserContext);

  const handleClickOnUser = () => {
    contextUser.setWatchingOtherProfileId(id);
  };

  return (
    <div className={styles.container}>
      <button
        onClick={handleClickOnUser}
        type="button"
        style={{background: 'none'}}
      >
        <div
          className={styles.imageWrapper}
          style={{ backgroundImage: pictureUrl && pictureUrl !== '' ? `url(${pictureUrl})` : `url(${profileImage})` }}
        />
      </button>
      <div className={styles.nameDescriptionWrapper}>
        <p className={styles.name}>
          {`${name} ${surname}`}
        </p>
        <p className={styles.description}>
          {description}
        </p>
      </div>
      <div className={styles.buttonWrapper}>
        {
          textButton
          && colorButton && (
            <Button
              text={textButton}
              color={colorButton}
              onClick={onClick}
              backgroundFull={backgroundFull}
            />
          )
        }
      </div>
    </div>
  );
};
export default ProfileCard;
