import React, { useState, useEffect, useReducer } from 'react';
import useFetch from 'use-http';
import styles from './Login.module.scss';
import Button from '../../components/Button';
import TextInput from '../../components/TextInput';
import * as users from '../../routes/users';

function Login() {
  const [signinEmail, setSiginEmail] = useState('');
  const [signinPassword, setSiginPassword] = useState('');

  const [signupNick, setSignupNick] = useState('');
  const [signupEmail, setSignupEmail] = useState('');
  const [signupPassword, setSignupPassword] = useState('');

  const { loading, error, data = [] } = useFetch(users.getAll(), {}, []);



  useEffect(() => console.log(signinEmail), [signinEmail]);

  const onChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSiginEmail(e.currentTarget.value);
  };

  return (
    <div className={styles.container}>
      <div className={styles.container}>
        <div className={styles.signinContainer}>
          <div className={styles.titleWrapper}>
            <h1 className={styles.title}>Already have an account?</h1>
          </div>
          <form method="post">
            <div className={styles.inputWrapper}>
              <TextInput onChange={onChangeHandler} type="email" placeholder="Email" />
            </div>
            <div className={styles.inputWrapper}>
              <TextInput type="password" placeholder="Password" />
            </div>
            <Button backgroundFull text="Sign in" color="blue" />
          </form>
        </div>

        <div className={styles.signupContainer}>
          <div className={styles.titleWrapper}>
            <h1 className={styles.title}>Don&apos;t have an account?</h1>
          </div>
          <form method="post">
            <div className={styles.inputWrapper}>
              <TextInput type="text" placeholder="Username" />
            </div>
            <div className={styles.inputWrapper}>
              <TextInput type="email" placeholder="Email" />
            </div>
            <div className={styles.inputWrapper}>
              <TextInput type="password" placeholder="Password" />
            </div>
            <Button backgroundFull text="Join" color="blue" />
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
