import React, {
  useState, useContext, useEffect, useReducer,
} from 'react';
import TextInput from '../../../components/TextInput';
import { User } from '../../../interfaces';
import UserContext from '../../../helpers/context';
import styles from './Signup.module.scss';
import * as authEndpoints from '../../../endpoints/auth';
import Button from '../../../components/Button';

const Signup = () => {
  // React-context to access current connected user
  const contextUser = useContext(UserContext);
  // Signup state
  const initialState = {
    signupName: '',
    signupSurname: '',
    signupEmail: '',
    signupPassword: '',
  };
  type SignupActionType = 'supName' | 'supSurname' | 'supEmail' | 'supPassword'
  type SignupAction = { type: SignupActionType; payload: string };

  const reducer = (signupState: typeof initialState, action: SignupAction) => {
    switch (action.type) {
      case 'supName':
        return { ...signupState, signupName: action.payload };
      case 'supSurname':
        return { ...signupState, signupSurname: action.payload };
      case 'supEmail':
        return { ...signupState, signupEmail: action.payload };
      case 'supPassword':
        return { ...signupState, signupPassword: action.payload };
      default:
        return signupState;
    }
  };
  const [signupState, dispatch] = useReducer(reducer, initialState);

  // Signup email change handler
  const onSignupStateHandler = (e: React.ChangeEvent<HTMLInputElement>, SignupActionType: SignupActionType) => {
    dispatch({ type: SignupActionType, payload: e.currentTarget.value });
  };

  // Function triggered when submiting sign up
  const onSignupSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const newUser: User = {
      email: signupState.signupEmail,
      password: signupState.signupPassword,
      name: signupState.signupName,
      surname: signupState.signupSurname,
    };
    try {
      const res: { user: User, token: string } = await authEndpoints.signup(newUser);
      if (res) {
        // Sets current user in context
        await contextUser.setUser(res.user);
        // Pushes to home screen
        history.push('./home');
      }
    } catch (err) {
      history.push('./notfound');
    }
  };


  const signupRender = (
    <div className={styles.signupContainer}>
      <div className={styles.titleWrapper}>
        <h1 className={styles.title}>Don&apos;t have an account?</h1>
      </div>
      <form onSubmit={onSignupSubmit}>
        <div className={styles.inputWrapper}>
          <TextInput onChange={(e) => onSignupStateHandler(e, 'supName')} type="text" placeholder="Name" />
        </div>
        <div className={styles.inputWrapper}>
          <TextInput onChange={(e) => onSignupStateHandler(e, 'supSurname')} type="text" placeholder="Surname" />
        </div>
        <div className={styles.inputWrapper}>
          <TextInput onChange={(e) => onSignupStateHandler(e, 'supEmail')} type="email" placeholder="Email" />
        </div>
        <div className={styles.inputWrapper}>
          <TextInput
            onChange={(e) => onSignupStateHandler(e, 'supPassword')}
            type="password"
            placeholder="Password"
            minLength={6}
          />
        </div>
        <Button
          backgroundFull
          text="Join"
          color="blue"
          type="submit"
        />
      </form>
    </div>
  );

};



export default Signup;
