import React, { useState } from 'react';
import useFetch from 'use-http';
import { useHistory } from 'react-router-dom';
import styles from './Login.module.scss';
import Button from '../../components/Button';
import TextInput from '../../components/TextInput';
import * as auth from '../../routes/auth';
import { User } from '../../interfaces/User';

function Login() {
  const history = useHistory();
  const { post: signinPost, response: signInResponse } = useFetch(auth.signin());
  const { post: signupPost, response: signupResponse } = useFetch(auth.signup());

  // Signin state
  const [signinEmail, setSigninEmail] = useState('');
  const [signinPassword, setSigninPassword] = useState('');

  // Signup state
  const [signupNick, setSignupNick] = useState('');
  const [signupEmail, setSignupEmail] = useState('');
  const [signupPassword, setSignupPassword] = useState('');

  // Signin email
  const onSigninEmailChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSigninEmail(e.currentTarget.value);
  };

  // Signin password
  const onSigninPasswordChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSigninPassword(e.currentTarget.value);
  };

  // Signup nick
  const onSignupNickChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSignupNick(e.currentTarget.value);
  };

  // Signup email
  const onSignupEmailChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSignupEmail(e.currentTarget.value);
  };

  // Signup password
  const onSignupPasswordChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSignupPassword(e.currentTarget.value);
  };

  // Function triggered when submiting sign in
  const onSigninSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const login = {
      email: signinEmail,
      password: signinPassword,
    };
    try {
      await signinPost(login);
      if (signInResponse.ok) {
        history.push('./home');
      }
    } catch (err) {
      history.push('./notfound');
    }
  };

  // Function triggered when submiting sign up
  const onSignupSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const newUser: User = {
      email: signupEmail,
      password: signupPassword,
      nick: signupNick,
    };
    try {
      await signupPost(newUser);
      history.push('./home');
    } catch (err) {
      history.push('./notfound');
    }
  };

  const signinRender = (
    <div className={styles.signinContainer}>
      <div className={styles.titleWrapper}>
        <h1 className={styles.title}>Already have an account?</h1>
      </div>
      <form onSubmit={onSigninSubmit}>
        <div className={styles.inputWrapper}>
          <TextInput
            onChange={onSigninEmailChangeHandler}
            type="email"
            placeholder="Email"
          />
        </div>
        <div className={styles.inputWrapper}>
          <TextInput
            onChange={onSigninPasswordChangeHandler}
            type="password"
            placeholder="Password"
          />
        </div>
        <Button
          backgroundFull
          text="Sign in"
          color="blue"
          type="submit"
        />
      </form>
    </div>
  );

  const signuprender = (
    <div className={styles.signupContainer}>
      <div className={styles.titleWrapper}>
        <h1 className={styles.title}>Don&apos;t have an account?</h1>
      </div>
      <form onSubmit={onSignupSubmit}>
        <div className={styles.inputWrapper}>
          <TextInput onChange={onSignupNickChangeHandler} type="text" placeholder="Nick" />
        </div>
        <div className={styles.inputWrapper}>
          <TextInput onChange={onSignupEmailChangeHandler} type="email" placeholder="Email" />
        </div>
        <div className={styles.inputWrapper}>
          <TextInput
            onChange={onSignupPasswordChangeHandler}
            type="password"
            placeholder="Password"
            title="The password must contain between 6 and 12 carachters, one uppercase letter, one number and one special carachter"
            pattern="^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*_=+-]).{6,12}$"
          />
        </div>
        <Button
          backgroundFull
          text="Join"
          color="blue"
          type="submit"
        />
      </form>
    </div>
  );

  return (
    <div className={styles.container}>
      <div className={styles.container}>
        {signinRender}
        {signuprender}
      </div>
    </div>
  );
}

export default Login;
