import React, { useContext } from 'react';
import styles from './PostCard.module.scss';
import profileImage from '../../assets/images/profileImage.png';
import { UserContext } from '../../containers/App';

type PostCardProps = {
  name: string,
  message: string,
  pictureUrl?: string,
  surname: string,
  last: boolean,
  timestamp: Date,
}

const PostCard = ({
  name, surname, message = '', pictureUrl, last, timestamp,
}: PostCardProps) => {
  const contextUser = useContext(UserContext);
  if (contextUser.watchingOtherProfileId) {
    return (
      <div className={last ? styles.lastContainer : styles.container}>
        <div className={styles.nameMessageWrapperSeeingProfile}>
          <p className={styles.message}>
            {message}
          </p>
        </div>
      </div>
    );
  }
  return (
    <div className={last ? styles.lastContainer : styles.container}>
      <div
        className={styles.imageWrapper}
        style={{ backgroundImage: pictureUrl && pictureUrl !== '' ? `url(${pictureUrl})` : `url(${profileImage})` }}
      />
      <div className={styles.nameMessageWrapper}>
        <div className={styles.nameWrapper}>
          <p className={styles.name}>
            {`${name} ${surname}`}
          </p>
        </div>
        <p>{timestamp}</p>
        <p className={styles.message}>
          {message}
        </p>
      </div>
    </div>
  );
};

export default PostCard;
