import React, {
  useState, useEffect, useContext, useRef,
} from 'react';
import TextInput from '../TextInput';
import ProfileCard from '../ProfileCard';
import { User } from '../../interfaces/User';
import * as userEndpoints from '../../endpoints/user';
import { Search } from '../../assets/icons';
import styles from './SearchBar.module.scss';
import { UserContext } from '../../containers/App';

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [userResults, setUserResults] = useState<User[]>([]);

  // Ref for the broken down results div
  const wrapperRef = useRef<HTMLDivElement>(null);

  // Ref for the search input
  const inputRef = useRef<HTMLInputElement>(null);

  // Reads current connected user from Context
  const contextUser = useContext(UserContext);

  // Sets searched term while writing
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.currentTarget.value);
  };

  // Makes a search every time the user inputs a carachter
  useEffect(() => {
    const fetchUsers = async () => {
      const users: User[] = await userEndpoints.getFilteredUsers(searchTerm);
      setUserResults(users);
    };
    // Performs search only if the searchTerm is not empty. If it is not
    // empty (e.g., cleaning the input), then the results must change to empty
    if (searchTerm) {
      fetchUsers();
    } else {
      setUserResults([]);
    }
  }, [searchTerm]);

  const unfollowUser = async (event: React.MouseEvent<HTMLButtonElement>, clickedUser: User) => {
    const { user: currentUser } = contextUser;
    const followingModified = currentUser!.following!.filter((el) => el.user !== clickedUser._id);
    const modifiedUser: User = { ...currentUser!, following: followingModified };
    // Modify user in context
    await contextUser.setUser(modifiedUser);
    // Modify list of followed users in database
    await userEndpoints.update(modifiedUser);
  };

  const followUser = async (event: React.MouseEvent<HTMLButtonElement>, clickedUser: User) => {
    const contextUserCopy: User = { ...contextUser!.user! };
    contextUserCopy.following!.push({ user: clickedUser._id! });
    // Modify user in context
    await contextUser.setUser(contextUserCopy);
    // Modify list of followed users in database
    await userEndpoints.update(contextUserCopy);
  };

  const usersList = userResults.map((user: User) => {
    const usersFollowing = contextUser.user?.following;
    const allUserIds = usersFollowing?.map((element: { user: string }) => element.user);
    const isFollowing = allUserIds?.includes(user._id!);
    if (isFollowing) {
      return (
        <div
          key={user._id}
          className={styles.targetWrapper}
        >
          <ProfileCard
            description={user.description}
            name={user.name}
            surname={user.surname}
            pictureUrl={user.profileImage?.url}
            onClick={(e) => unfollowUser(e, user)}
            textButton="Unfollow"
            colorButton="red"
          />
        </div>
      );
    }
    return (
      <div
        key={user._id}
        className={styles.targetWrapper}
      >
        <ProfileCard
          description={user.description}
          name={user.name}
          surname={user.surname}
          pictureUrl={user.profileImage?.url}
          onClick={(e) => followUser(e, user)}
          textButton="Follow"
          colorButton="blue"
        />
      </div>
    );
  });

  // sets if the user is writing in the search input or not
  useEffect(() => {
    if (searchTerm) {
      contextUser.setIsSearching(true);
    } else {
      contextUser.setIsSearching(false);
    }
  }, [searchTerm]);

  // closes the search results if the user clicks outside
  useEffect(() => {
    // Detects if clicked on outside of element
    function handleClickOutside(event: any) {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
        console.log('clickaste fuera');
        // setSearchTerm('');
        setUserResults([]);
        contextUser.setIsSearching(false);
      }
    }
    console.log('ataching');
    // Bind the event listener
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      // Unbind the event listener on clean up
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [wrapperRef]);

  const brokenDownSearch = () => {
    if (contextUser.isSearching && userResults.length > 0) {
      return (
        <div
          className={styles.resultsWrapperShow}
          ref={wrapperRef}
        >
          {usersList}
        </div>
      );
    }
    if (contextUser.isSearching && userResults.length === 0) {
      return (
        <div
          className={styles.resultsWrapperShow}
          ref={wrapperRef}
        >
          No se encontraron resultados
        </div>
      );
    }
  };

  return (
    <>
      <TextInput
        type="text"
        placeholder="Search"
        onChange={handleChange}
        ref={inputRef}
      >
        <Search />
      </TextInput>
      {/* Show div with results when the results array has data */}
      {brokenDownSearch()}
    </>
  );
};

export default SearchBar;
