import * as React from 'react';
import styles from './Button.module.scss';

type ButtonProps = {
  backgroundFull?: boolean;
  text: string;
  color: 'blue' | 'red';
}

const Button = ({ backgroundFull = false, text, color = 'blue' }: ButtonProps) => {
  let classN = '';
  if (backgroundFull && color === 'blue') {
    classN = 'fullBlueButton';
  }
  if (backgroundFull && color === 'red') {
    classN = 'fullRedButton';
  }
  if (!backgroundFull && color === 'blue') {
    classN = 'emptyBlueButton';
  }
  if (!backgroundFull && color === 'blue') {
    classN = 'emptyBlueButton';
  }
  return (
    <button
      type="button"
      className={classN}
    >
      {text}
    </button>
  );
};

export default Button;
