// const baseUrl = 'https://backend-post-application.herokuapp.com/api/users/';
const baseUrl = 'http://localhost:5000/api/users/';

// Get
const exists = () => `${baseUrl}exists-user`;
const getAll = () => `${baseUrl}`;
const get = (id: string) => `${baseUrl}${id}`;
const getFollowing = (id: string) => `${baseUrl}following/${id}`;
const getFollowers = (id: string) => `${baseUrl}followers/${id}`;

// Post
const create = () => `${baseUrl}`;

// Put
const update = (id: string) => `${baseUrl}${id}`;

// Delete
const deleteById = (id: string) => `${baseUrl}${id}`;

export {
  exists, getAll, get, create, update, deleteById, getFollowing, getFollowers,
};
