import React from 'react';
import styles from './ProfileTarget.module.scss';
import Button from '../Button';
import profileImage from '../../assets/images/profileImage.png';

type ProfileTargetProps = {
  name: string,
  description?: string,
  pictureUrl?: string,
  surname: string,
  onClick?: () => void,
}

const ProfileTarget = ({
  name, surname, description = '', pictureUrl = profileImage, onClick,
}: ProfileTargetProps) => (
  <div className={styles.container}>
    <div
      className={styles.imageWrapper}
      style={{ backgroundImage: `url(${pictureUrl})` }}
    />
    <div className={styles.nameDescriptionWrapper}>
      <p className={styles.name}>
        {`${name} ${surname}`}
      </p>
      <p className={styles.description}>
        {description}
      </p>
    </div>
    <Button
      text="Unfollow"
      color="blue"
      onClick={onClick}
    />
  </div>
);

export default ProfileTarget;
