import React, { useState, useContext, useRef } from 'react';
import styles from './PostSection.module.scss';
import Button from '../../../components/Button';
import { Post } from '../../../interfaces';
import { UserContext } from '../../App';
import * as postEndpoints from '../../../endpoints/post';

const PostSection = () => {
  const [post, setPost] = useState('');
  const onChangeTextHandler: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    setPost(event.target.value);
  };
  const inputRef = useRef<HTMLInputElement | null>(null);
  // Reads current connected user from Context
  const contextUser = useContext(UserContext);

  // Function triggered when submiting sign up
  const handlePost = async (event: React.FormEvent<HTMLFormElement>) => {
    setPost('');
    event.preventDefault();
    const newPost: Post = {
      author: contextUser.user!._id!,
      date: new Date(),
      text: post,
    };
    const res: { post: Post } = await postEndpoints.create(newPost);
    if (res) {
      // Reloads reading section TODO
    }
  };

  return (
    <section className={styles.postSection}>
      <form onSubmit={handlePost}>
        <input
          placeholder="What do you want to write?"
          className={styles.textInput}
          required
          onChange={onChangeTextHandler}
          value={post}
          type="text"
          maxLength={200}
          ref={inputRef}
        />
        <Button
          backgroundFull
          text="Post"
          color="blue"
          type="submit"
        />
      </form>
    </section>
  );
};

export default PostSection;
