import React, { useState, useEffect } from 'react';
import useFetch from 'use-http';
import ProfileTarget from '../../../components/ProfileTarget';
import styles from './FollowSection.module.scss';
import * as usersRoutes from '../../../routes/usersRoutes';

const FollowSection = () => {
  const testId = '5f1de9796dee4e331c9913b9';
  const testUrl = 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.vectorstock.com%2Froyalty-free-vector%2Fmale-profile-avatar-with-brown-hair-vector-12055105&psig=AOvVaw0NdJzYRbZBmVdcYzBl2m35&ust=1595900161987000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCMi49sOl7OoCFQAAAAAdAAAAABAD';

  const {
    loading: followingLoading, error: followingError, get: followingGet, response: followingResponse,
  } = useFetch(usersRoutes.getFollowing(testId));

  const {
    loading: followersLoading, error: followersError, get: followersGet, response: followersResponse, 
  } = useFetch(usersRoutes.getFollowers(testId));

  const [followingTabActive, setFollowingTabActive] = useState(true);
  const [followersTabActive, setFollowersTabActive] = useState(false);
  const [following, setFollowing] = useState([]);
  const [followers, setFollowers] = useState([]);

  // Loads following people after first render
  useEffect(() => {
    async function loadFollowing() {
      const data = await followingGet();
      if (followingResponse.ok) {
        setFollowing(data);
      }
    }
    loadFollowing();
  }, []);

  useEffect(() => {console.log(following)},[])

  const handleFollowingClick = () => {
    if (!followingTabActive) {
      setFollowingTabActive(true);
      setFollowersTabActive(false);
    }
  };

  const handleFollowersClick = () => {
    if (!followersTabActive) {
      setFollowersTabActive(true);
      setFollowingTabActive(false);
    }
  };

  return (
    <>
      <div className={styles.tabList}>
        <button
          onClick={handleFollowingClick}
          className={followingTabActive ? styles.activeTab : styles.notActiveTab}
        >
          Following
        </button>
        <button
          onClick={handleFollowersClick}
          className={followersTabActive ? styles.activeTab : styles.notActiveTab}
        >
          Followers
        </button>
      </div>
      <ProfileTarget
        description="what the fuckk"
        name="Franchu"
        pictureUrl={testUrl}
      />
    </>
  );
};

export default FollowSection;
