import { useContext } from 'react';
import { UserContext } from '../containers/App';
import { loadUser, saveUser } from '../helpers/localStorage';
import * as userEndpoints from '../endpoints/user';

const useUser = () => {
  const currentUser = useContext(UserContext);
  console.log('currentUser')

  console.log(currentUser)
  if (currentUser.user) {
    return { user: currentUser.user, setUser: currentUser.setUser };
  }
  const localStoredUser = loadUser();
  if (localStoredUser) {
    const fetchUser = async () => {
      const res = await userEndpoints.getSingle(localStoredUser.id!);
      await currentUser.setUser(res);
      return { user: currentUser.user, setUser: currentUser.setUser };
    };
    fetchUser();
  }
  return undefined;
};

export default useUser;
