import React, { useState, useEffect, useContext } from 'react';
import style from './ProfileHeader.module.scss';
import { User } from '../../../../interfaces/User';
import * as userEndpoints from '../../../../endpoints/user';
import profileImage from '../../../../assets/images/profileImage.png';
import { UserContext } from '../../../App';
import { IoIosArrowRoundBack } from 'react-icons/io';

type ProfileHeaderProps = {
  userId: string,
}

const ProfileHeader = ({ userId }: ProfileHeaderProps) => {
  const [user, setUser] = useState<User>();

  // Reads current connected user from Context
  const contextUser = useContext(UserContext);

  // Reads user from database
  useEffect(() => {
    const getUser = async () => {
      const userFromDB = await userEndpoints.getSingle(userId);
      setUser(userFromDB);
    };
    getUser();
  }, [userId]);

  // stops watching a particular profile and comes back to see posts from all followed users
  const stopWatchingProfile = () => {
    contextUser.setWatchingOtherProfileId('');
  };

  return (
    <div className={style.profileHeaderWrapper}>
      <button className={style.backButton} type="button" onClick={stopWatchingProfile}>
        <IoIosArrowRoundBack size={'100px'} color={'white'} style={{backgroundColor: 'red'}}/>
      </button>
      <div
        className={style.imageWrapper}
        style={{
          backgroundImage: user?.profileImage?.url && user?.profileImage?.url !== ''
            ? `url(${user?.profileImage?.url})` : `url(${profileImage})`,
        }}
      />
      <div className={style.container}>
        <p className={style.name}>{`${user?.name} ${user?.surname}`}</p>
        <p className={style.description}>{user?.description}</p>
        <ul className={style.infoWrapper}>
          <li className={style.listItem}>
            <span className={style.spanlistItem}>{user?.followers?.length}</span>
            {' followers'}
          </li>
          <li className={style.listItem}>
            <span className={style.spanlistItem}>{user?.following?.length}</span>
            {' following'}
          </li>
        </ul>
      </div>
    </div>
  );
};

export default ProfileHeader;
