import React, { useState, useEffect, useContext } from 'react';
import style from './ReadingSection.module.scss';
import PostCard from '../../../components/PostCard';
import { Post } from '../../../interfaces/Post';
import { UserContext } from '../../App';
import * as userEndpoints from '../../../endpoints/user';

const ReadingSection = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const contextUser = useContext(UserContext);

  // Realoads posts from followed users every time the user
  // changes (for instance, when following/unfollowing users).
  // It also reloads when the user sends a post
  useEffect(() => {
    const getPostsFromAllFollowedusers = async () => {
      const followedUsersPosts: Post[] =
        await userEndpoints.getPostsFromFollowedUsers(contextUser.user!._id!);
      const ownPosts: Post[] = await userEndpoints.getUsersPosts(contextUser.user!._id!);
      setPosts([...followedUsersPosts, ...ownPosts]);
      console.log('loading all');
    };
    const getPostsFromOneUser = async () => {
      const usersPosts: Post[] =
        await userEndpoints.getUsersPosts(contextUser.watchingOtherProfileId);
      setPosts([...usersPosts]);
      console.log('loading one ');
    };
    contextUser.watchingOtherProfileId ? getPostsFromOneUser() : getPostsFromAllFollowedusers();
  }, [contextUser]);

  // const seePostsFromUser = async () => {
  //   const posts = await getUsersPosts(id);
  //   contextUser.watchingOtherProfileId(id);
  // }

  useEffect(() => {
    console.log('seeing profile');
    console.log(contextUser.watchingOtherProfileId);
  }, [contextUser.watchingOtherProfileId]);

  const postsList = posts.map((post: Post) => (
    <React.Fragment key={post._id}>
      <PostCard
        name={post.author.name}
        surname={post.author.surname}
        message={post.text}
        pictureUrl={post.author.profileImage?.url}
      />
    </React.Fragment>
  ));

  return (
    <section className={style.readingSection}>
      {postsList}

    </section>
  );
};

export default ReadingSection;
