import React, {
  useState, useEffect, useReducer, useRef,
} from 'react';
import useFetch from 'use-http';
import styles from './Login.module.scss';
import Button from '../../components/Button';
import TextInput from '../../components/TextInput';
import * as users from '../../routes/users';
import { User } from '../../interfaces/User';
import * as helpers from '../../helpers';

function Login() {
  // Signin state
  const [signinEmail, setSigninEmail] = useState('');
  const [signinEmailError, setSigninEmailError] = useState('');
  const [signinPassword, setSigninPassword] = useState('');
  const [signinPasswordError, setSigninPasswordError] = useState('');

  // Signup state
  const [signupNick, setSignupNick] = useState('');
  const [signupEmail, setSignupEmail] = useState('');
  const [signupEmailError, setSignupEmailError] = useState('');
  const [signupPassword, setSignupPassword] = useState('');
  const [signupPasswordError, setSignupPasswordError] = useState('');

  const { post } = useFetch(users.create());
  const signupButtonRef = React.useRef() as React.MutableRefObject<HTMLInputElement>;
  // const { loading, error, data = [] } = useFetch(users.getAll(), {}, []);

  // Signin email
  const onSigninEmailChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSigninEmail(e.currentTarget.value);
  };

  // Signin password
  const onSigninPasswordChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSigninPassword(e.currentTarget.value);
  };

  // Signup nick
  const onSignupNickChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSignupNick(e.currentTarget.value);
  };

  // Signup email
  const onSignupEmailChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSignupEmail(e.currentTarget.value);
  };

  // Signup password
  const onSignupPasswordChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSignupPassword(e.currentTarget.value);
  };

  const onSigninSubmit = async () => {
    console.log('dispara 1');
    const auth = {
      email: signinEmail,
      password: signinPassword,
    };
    console.log('dispara 2');
    const res = await post(auth);
    console.log('res is ');
    console.log(res);
  };

  const onSignupSubmit = async () => {
    const emailError = helpers.validateEmail(signupEmail);
    setSignupEmailError(emailError);
    const passwordError = helpers.validatePassword(signupPassword);
    setSignupPasswordError(passwordError);
    if (emailError === '' && passwordError === '') {
      const newUser: User = {
        email: signinEmail,
        password: signinPassword,
        nick: signupNick,
      };
      const res = await post(newUser);
      console.log('res is ');
      console.log(res);
    }
  };

  useEffect(() => {
    console.log('current')
    console.log(signupButtonRef.current)
    signupButtonRef.current.focus();
  },
    []);

  return (
    <div className={styles.container}>
      <div className={styles.container}>
        <div className={styles.signinContainer}>
          <div className={styles.titleWrapper}>
            <h1 className={styles.title}>Already have an account?</h1>
          </div>
          <form method="post">
            <div className={styles.inputWrapper}>
              <TextInput onChange={onSigninEmailChangeHandler} type="email" placeholder="Email" />
            </div>
            <div className={styles.inputWrapper}>
              <TextInput onChange={onSigninPasswordChangeHandler} type="password" placeholder="Password" />
            </div>
            <Button backgroundFull text="Sign in" color="blue" type="submit" onClick={onSigninSubmit} />
          </form>
        </div>

        <div className={styles.signupContainer}>
          <div className={styles.titleWrapper}>
            <h1 className={styles.title}>Don&apos;t have an account?</h1>
          </div>
          <form method="post">
            <div className={styles.inputWrapper}>
              <TextInput onChange={onSignupNickChangeHandler} type="text" placeholder="Nick" />
            </div>
            <div className={styles.inputWrapper}>
              <TextInput onChange={onSignupEmailChangeHandler} type="email" placeholder="Email" />
            </div>
            <div className={styles.inputWrapper}>
              <TextInput onChange={onSignupPasswordChangeHandler} pattern="/^.*(?=.*[A-Z]).*$/" type="password" placeholder="Password" ref={signupButtonRef} />
            </div>
            <Button backgroundFull text="Join" color="blue" type="submit" onClick={onSignupSubmit} />
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
