import React from 'react';
import styles from './ProfileCard.module.scss';
import Button from '../Button';
import profileImage from '../../assets/images/profileImage.png';

type ProfileCardProps = {
  name: string,
  description?: string,
  pictureUrl?: string,
  surname: string,
  textButton?: string,
  colorButton: 'blue' | 'red';
  onClick?: (event: React.MouseEvent<HTMLButtonElement>) => void,
  backgroundFull?: boolean,
}

const ProfileCard = ({
  name, surname, description = '', pictureUrl, onClick, textButton, colorButton, backgroundFull,
}: ProfileCardProps) => (
  <div className={styles.container}>
    <div
      className={styles.imageWrapper}
      style={{ backgroundImage: pictureUrl && pictureUrl !== '' ? `url(${pictureUrl})` : `url(${profileImage})` }}
    />
    <div className={styles.nameDescriptionWrapper}>
      <p className={styles.name}>
        {`${name} ${surname}`}
      </p>
      <p className={styles.description}>
        {description}
      </p>
    </div>
    <div
      className={styles.buttonWrapper}
    >
      {
          textButton
          && (
          <Button
            text={textButton}
            color={colorButton}
            onClick={onClick}
            backgroundFull={backgroundFull}
          />
          )
        }
    </div>
  </div>
);

export default ProfileCard;
