import React, { useState, useEffect } from 'react';
import TextInput from '../TextInput';
import ProfileCard from '../ProfileCard';
import { User } from '../../interfaces/User';
import * as userEndpoints from '../../endpoints/user';

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [userResults, setUserResults] = useState<User[]>([]);

  // Sets searched term while writing
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.currentTarget.value);
  };

  // Makes a search every time the user inputs a carachter
  useEffect(() => {
    const fetchUsers = async () => {
      const users: User[] = await userEndpoints.getPostsFromFollowedUsers(searchTerm);
      setUserResults(users);
    };
  }, [searchTerm]);

  const searchWindow = userResults.map((user: User) => {
    return (
      <ProfileCard
        description={user.description}
        name={user.name}
        surname={user.surname}
        pictureUrl={user.profileImage?.url}
        onClick={(e) => unfollowUser(e, user)}
        textButton="Unfollow"
        colorButton="red"
      />
    )
  });

  return (
    <div>
      <TextInput type="text" placeholder="Search" onChange={handleChange} />
      {searchTerm && searchWindow}
    </div>
  );
};

export default SearchBar;
