const validateEmail = (emailAddress: string) => {
  // eslint-disable-next-line no-useless-escape
  const reg = /\S+@\S+\.\S+/;
  if (!reg.test(emailAddress)) {
    return 'Write a valid email addres';
  }
  return '';
};

export default validateEmail;
